const axios = require('axios');

function getDrinks(){
    axios.get("http://localhost:4000/drinks", (req,res)=>{console.log(res)})
}

getDrinks();


document.querySelector("monsterbtn").addEventListener("click", handleclick);


function handleclick(){
    alert("i got clicked");
}
